INSERT INTO `clt_article` VALUES (5,35,1,'admin','网络营销要注意规避口碑营销','color:#555','网络营销要注意规避口碑营销','','','网络营销要注意规避口碑营销','<p>网络营销要注意规避口碑营销</p>','0','',0,1,1,'',0,0,'',61,1481700574,1484645988);
INSERT INTO `clt_article` VALUES (6,42,1,'admin','高薪前端开发工程师','color:#555','前端开发','','','前端开发人员3名','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; color: gray;\">岗位要求：<br/>1、18-30岁，专业不限，高中及以上学历者优先<br/>2、需对计算机、游戏开发、设计、网站建设有兴趣<br/>3、有一定的逻辑思维，会简单电脑操作<br/>4、面试通过，直接上岗，无经验者，人品优秀可先带薪实习后上岗<br/>福利待遇：<br/>1、周末双休，每天7小时上班时间，无夜班，不加班，<br/>2、大量有奖金项目可做，能力强者可不限量接受奖金项目，<br/>3、公司为员工购买五险一金，定期组织员工生日会、野外游玩等活动，<br/>4、提供良好的晋升机制，广阔的晋升空间。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; color: gray;\">注：有意者可直接投递简历，我们会在最短的时间与您取得联系，安排面试时间</span></p><p><br/></p>','0','',0,1,1,'',0,0,'',4,1484704707,0);
INSERT INTO `clt_article` VALUES (7,42,1,'admin','高薪PHP技术开发工程师','color:#555','高薪PHP技术开发工程师','','','高薪PHP技术开发工程师','<p style=\"margin: 0cm 0cm 0pt; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><span style=\"margin: 0px; padding: 0px; font-size: 14pt;\">岗位条件：<span style=\"margin: 0px; padding: 0px;\"></span></span></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size:16px;font-family:宋体\"></span></p><p style=\"margin: 0cm 0cm 0pt; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 14pt;\">1</span></span><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 14pt;\">、<span style=\"margin: 0px; padding: 0px;\">18-29</span>岁，对计算机有浓厚的兴趣，喜欢互联网工作<span style=\"margin: 0px; padding: 0px;\"></span></span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size:16px;font-family:宋体\"></span></p><p style=\"margin: 0cm 0cm 0pt; padding: 0px; color: rgb(40, 40, 40); font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; text-indent: 0cm; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 14pt;\"><span style=\"margin: 0px; padding: 0px;\">2、</span></sp